 suspend fun readTimeout2(
        inputStream: InputStream,
        groupDataTime: Long = 100L,
        timeout: Long = TaskConfig.TIME_OUT_TIME,
        //是否打断正在等待检测超时
        hasTaskCall: () -> Boolean
    ): ByteArray = withContextIO {
        val readBytes = ByteArrayOutputStream()
        val startTime = System.currentTimeMillis()
        var available: Int
        var hasTask: Boolean
        // 循环执行获取数据 如果没有数据等待超时,如果有数据则继续下次循环,

        // Loop until timeout or task finishes or data becomes available
        while (true) {
            delay(groupDataTime)
            hasTask = hasTaskCall()
            available = inputStream.available()
            //组包时间内 如果没有task 并且没有数据,则等待超时
            if (available <= 0) {
                // 如果task存在,则退出循环
                if (hasTask) {
                    break
                }
                // 超时退出
                if (System.currentTimeMillis() - startTime > timeout) {
                    break
                }
            }
            //有数据
            val tempBytes = ByteArray(available)
            val readSize = inputStream.read(tempBytes)
            if (readSize > 0) {
                readBytes.write(tempBytes, 0, readSize)
            }
        }
        return@withContextIO readBytes.toByteArray()
    }

/**
     * [waitTask] 当前是否等待task执行完成
     * [hasTaskCall] 后续是否有待执行task
     * [groupDataTime] 检测数据包时间
     * [timeout] 检测超时时间
     */
    suspend fun readTimeout2(
        waitTask: Boolean,
        inputStream: InputStream,
        groupDataTime: Long = 100L,
        timeout: Long = TaskConfig.TIME_OUT_TIME,
        hasTaskCall: () -> Boolean
    ): ByteArray = withContextIO {
        val readBytes = ByteArrayOutputStream()
        val startTime = System.currentTimeMillis()
        var available: Int
        var hasTask: Boolean
        // 循环执行获取数据 如果没有数据等待超时,如果有数据则继续下次循环,
        while (true) {
            delay(groupDataTime)
            hasTask = hasTaskCall()
            available = inputStream.available()
            //组包时间内 如果没有task 并且没有数据,则等待超时
            if (available <= 0) {
                //如果当前非等待task, 如果后续有待执行task,则退出循环
                if (!waitTask && hasTask) {
                    break
                }
                // 当前有等待task,则等待超时
                if (System.currentTimeMillis() - startTime > timeout) {
                    break
                }
                continue
            }
            //有数据
            val tempBytes = ByteArray(available)
            val readSize = inputStream.read(tempBytes)
            if (readSize > 0) {
                readBytes.write(tempBytes, 0, readSize)
            }
        }
        return@withContextIO readBytes.toByteArray()
    }
